export default function updateUserName(userItem, editModalTextValue, updateUser, dispatch) {
  const newUser = { ...userItem };
  if (editModalTextValue.length) {
    newUser.userName = editModalTextValue;
  } else {
    newUser.userName = 'New User';
  }
  dispatch(updateUser(newUser._id, newUser));
}
