export default function addImage(
  character,
  defaultImage,
  imageUri,
  updateCharacterFunction,
  dispatch,
) {
  debugger;
  const newCharacter = { ...character };
  const defaultImageBase64 = `data:image/jpg;base64,${defaultImage.base64}`;

  function imageToBase64(image) {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    canvas.width = image.width;
    canvas.height = image.height;

    image.crossOrigin = 'anonymous';

    ctx.drawImage(image, 0, 0);

    return canvas.toDataURL();
  }

  const uriStart = imageUri.slice(0, 4);

  const newImage = {
    imageBase64: imageUri,
    uniqueKey: `${performance.now()}${performance.now()}`,
  };

  if (uriStart === 'file') {
    newImage.imageBase64 = imageToBase64(imageUri);
  } else if (!newImage.imageBase64.length) {
    newImage.imageBase64 = defaultImageBase64;
  } else {
    newImage.imageBase64 = uriStart;
  }

  newCharacter.sheet.uploads.push(newImage);

  dispatch(updateCharacterFunction(newCharacter._id, newCharacter));
}
