import React, { useState, useEffect } from 'react';
import {
  ImageBackground, Text, View, ScrollView,
} from 'react-native';
import { connect } from 'react-redux';
import NavigationBar from '../NavigationBar/NavigationBar';
import styles from './CharacterNotesStyles';
import absalomBackground from '../../images/absalom-background.png';

function CharacterNotes({
  characterItem, userId, dispatch, navigation,
}) {
  const [character, setCharacter] = useState(characterItem);

  useEffect(() => {
    setCharacter(characterItem);
  });

  return (
    <ImageBackground source={absalomBackground} style={styles.backgroundImage}>
      <ScrollView style={styles.container}>
        {character ? (
          <View style={styles.notes}>
            <View style={styles.character__title}>
              <View style={styles.title__bar} />
              <Text style={styles.character__name}>{`${character.name} Notes`}</Text>
              <View style={styles.title__bar} />
            </View>
            {character.notes.characterNotes.map((note) => (
              <View style={styles.notes__note}>
                <View style={styles.inventory__category__title}>
                  <View style={styles.category__title__name}>
                    <TouchableOpacity
                      onPress={() => {
                        setCategoryTitle(category.title);
                        setCategoryKey(category.uniqueKey);
                        setEditCategoryModalView(!editCategoryModalView);
                        setEditCategoryModalValue(category.title);
                      }}
                    >
                      <Text style={styles.inventory__category__title__text}>{category.title}</Text>
                    </TouchableOpacity>
                  </View>
                  <TouchableOpacity
                    style={styles.category__title__addIcon}
                    onPress={() => {
                      setCategoryKey(category.uniqueKey);
                      setItemModalProperties([{ name: 'Item Name', value: '' }]);
                      setAddItemModalView(!addItemModalView);
                    }}
                  >
                    <Image source={tansparentAddIcon} style={styles.addIcon} />
                  </TouchableOpacity>
                </View>
              </View>
            ))}
          </View>
        ) : (
          <View style={styles.character__title}>
            <View style={styles.title__bar} />
            <Text style={styles.character__name}>No Character</Text>
            <View style={styles.title__bar} />
          </View>
        )}
      </ScrollView>
      <NavigationBar navigation={navigation} userId={userId} dispatch={dispatch} />
    </ImageBackground>
  );
}

function mapStateToProps({ charactersReducer, usersReducer }) {
  return {
    characterItem: charactersReducer.characterItem,
    userId: usersReducer.userId,
  };
}

export default connect(mapStateToProps)(CharacterNotes);
