/* eslint-disable react/prop-types */
import React, { useState, useEffect } from 'react';
import {
  ScrollView, ImageBackground, View, Text,
} from 'react-native';
import { connect } from 'react-redux';
import { loadUser } from '../../redux/actions/userActions';
import styles from './CharacterListStyles';
import absalomBackground from '../../images/absalom-background.png';

function CharacterList({ userItem, dispatch }) {
  const [characterList, setCharacterList] = useState(null);

  useEffect(() => {
    if (!characterList) {
      dispatch(loadUser('5fc3f007332636744ff4a820'));
      setCharacterList(userItem);
    }
  }, [userItem]);
  return (
    <ImageBackground source={absalomBackground} style={styles.backgroundImage}>
      <ScrollView style={styles.container}>
        {characterList ? (
          <View style={styles.character__title}>
            <View style={styles.title__bar} />
            <Text style={styles.character__name}>{userItem.userName}</Text>
            <View style={styles.title__bar} />
          </View>
        ) : (
          <Text>No user</Text>
        )}
      </ScrollView>
    </ImageBackground>
  );
}

function mapStateToProps({ usersReducer }) {
  return {
    userItem: usersReducer.userItem,
  };
}

export default connect(mapStateToProps)(CharacterList);
