export default function updateUserName(userItem, newValue, updateUser, dispatch, marker) {
  const newUser = { ...userItem };

  switch (marker) {
    case 'user':
      if (newValue.length) {
        newUser.userName = newValue;
      } else {
        newUser.userName = 'New User';
      }
      break;
    case 'games':
      newUser.favouriteGames = [...newValue];
      break;
    case 'languages':
      newUser.languages = [...newValue];
      break;
    default:
      break;
  }
  dispatch(updateUser(newUser._id, newUser));
}
