export default function addImage(
  {
    character,
    defaultImage,
    updateCharacterFunction,
    dispatch,
  },
  imageBase64,
) {
  debugger;
  const newCharacter = { ...character };
  const newImage = { ...imageItem };
  const defaultImageBase64 = `data:image/jpg;base64,${defaultImage.base64}`;

  if (!newImage.imageBase64.length) {
    newImage.imageBase64 = defaultImageBase64;
  }

  newCharacter.sheet.uploads.push(newImage);

  dispatch(updateCharacterFunction(newCharacter._id, newCharacter));
}
