/* eslint-disable react/prop-types */
import React, { useState, useEffect } from 'react';
import {
  ScrollView, ImageBackground, TextInput,
  View, Text, TouchableOpacity, Image,
} from 'react-native';
import { useIsFocused } from '@react-navigation/native';
import { connect } from 'react-redux';
import { Overlay } from 'react-native-elements';
import { loadUser, setUserId } from '../../redux/actions/userActions';
import { setCharacterId } from '../../redux/actions/characterActions';
import displayAddCharacterModal from './CharacterListFunctions';
import NavigationBar from '../NavigationBar/NavigationBar';
import styles from './CharacterListStyles';
import absalomBackground from '../../images/absalom-background.png';
import addIcon from '../../images/add-icon.png';

function CharacterList({
  userItem, userId, dispatch, navigation,
}) {
  debugger;
  const [characterList, setCharacterList] = useState([]);
  const [addCharacterModalView, setAddCharacterModalView] = useState(false);
  const isFocused = useIsFocused();

  const newCharacter = {
    name: '',
    game: '',
    race: '',
    lvl: '',
    characterClass: '',
    owner: '',
    sheet: {
      public: true,
      url: '',
    },
    inventory: {
      public: true,
      categories: [],
    },
    notes: {
      public: true,
      characterNotes: [],
    },
  };

  const [newCharacterName, setNewCharacterName] = useState('');
  const [newCharacterGame, setNewCharacterGame] = useState('');
  const [newCharacterRace, setNewCharacterRace] = useState('');
  const [newCharacterClass, setNewCharacterClass] = useState('');
  const [newCharacterLvl, setNewCharacterLvl] = useState('');

  useEffect(() => {
    if (!userId?.length) {
      dispatch(setUserId('5fc3f007332636744ff4a820'));
    } else if (!userItem?._id) {
      dispatch(loadUser(userId));
      setCharacterList([]);
    } else {
      setCharacterList(userItem?.characters);
    }
  }, [userItem, userItem?.characters, isFocused, userId]);

  return (
    <ImageBackground source={absalomBackground} style={styles.backgroundImage}>
      <ScrollView style={styles.container}>
        {characterList.length ? (
          <View style={styles.characterList}>
            <View style={styles.title__bar} />
            <Text style={styles.characterList__title}>{`${userItem.userName} Characters`}</Text>
            <View style={styles.title__bar} />
            <View style={styles.characterList__list}>
              {characterList.map((character) => (
                <TouchableOpacity
                  style={styles.characterList__list__block}
                  key={`${character._id}${Math.random()}`}
                  onPress={() => {
                    dispatch(setCharacterId(character._id));
                    navigation.navigate('CharacterDetail');
                  }}
                >
                  <View style={styles.characterList__list__block__row}>
                    <View style={styles.characterList__character__firstRow__name__block}>
                      <Text style={styles.characterList__character__firstRow__name__text}>
                        {character.name}
                      </Text>
                    </View>
                    <Text style={styles.characterList__character__firstRow__text}>
                      Level:
                      {' '}
                      {character.lvl}
                    </Text>
                  </View>
                  <View style={styles.characterList__list__block__row}>
                    <Text
                      id={Math.random()}
                      style={styles.characterList__character__secondRow__text}
                    >
                      {character.game}
                    </Text>
                    <Text
                      id={Math.random()}
                      style={styles.characterList__character__secondRow__text}
                    >
                      {character.race}
                    </Text>
                    <Text
                      id={Math.random()}
                      style={styles.characterList__character__secondRow__text}
                    >
                      {character.characterClass}
                    </Text>
                  </View>
                </TouchableOpacity>
              ))}
            </View>

          </View>
        ) : (
          <View style={styles.characterList}>
            <Text style={styles.characterList__title}>Loading...</Text>
          </View>
        )}
        <Overlay
          name="add character modal"
          isVisible={addCharacterModalView}
          overlayStyle={styles.addCharacterModal}
          backdropStyle={styles.addCharacterModal__background}
          animationType="fade"
          onBackdropPress={() => {
            displayAddCharacterModal(
              setAddCharacterModalView, addCharacterModalView,
            );
            setNewCharacterName('');
            setNewCharacterGame('');
            setNewCharacterRace('');
            setNewCharacterClass('');
            setNewCharacterLvl('');
          }}
        >
          <View style={styles.addCharacterModal__block}>
            <Text style={styles.character__description__text}>Add a new character</Text>
            <View>
              <Text>Name:</Text>
              <TextInput
                value={newCharacterName}
                onChangeText={(text) => setNewCharacterName(text)}
              />
            </View>
          </View>
        </Overlay>

      </ScrollView>
      <TouchableOpacity
        style={styles.addIcon__block}
        onPress={() => setAddCharacterModalView(!addCharacterModalView)}
      >
        <Image source={addIcon} style={styles.icon} />
      </TouchableOpacity>
      <NavigationBar navigation={navigation} dispatch={dispatch} />
    </ImageBackground>
  );
}

function mapStateToProps({ usersReducer }) {
  return {
    userItem: usersReducer.userItem,
    userId: usersReducer.userId,
  };
}

export default connect(mapStateToProps)(CharacterList);
