export function updateUser(userItem, newValue, updateUser, dispatch, marker) {
  const newUser = { ...userItem };
  const user = 'user';
  const games = 'games';
  const languages = 'languages';
  switch (marker) {
    case user:
      if (newValue.length) {
        newUser.userName = newValue;
      } else {
        newUser.userName = 'New User';
      }
      break;
    case games:
      newUser.favouriteGames = [...newValue];
      break;
    case languages:
      newUser.languages = [...newValue];
      break;
    default:
      break;
  }
  dispatch(updateUser(newUser._id, newUser));
}

export function deleteListValue(categoryArray, valueId, setEditListModalArrayValue) {
  debugger;
  const newCategoryArray = categoryArray.filter(
    (actualValue) => actualValue.uniqueKey !== valueId,
  );
  setEditListModalArrayValue(newCategoryArray);
}
