export function addImage(
  character,
  defaultImage,
  imageResult,
  updateCharacterFunction,
  dispatch,
) {
  debugger;
  const newCharacter = { ...character };

  const newImage = {
    imageBase64: imageResult.base64,
    uniqueKey: `${performance.now()}${performance.now()}`,
    width: imageResult.width,
  };

  if (!newImage.imageBase64.length) {
    newImage.imageBase64 = defaultImage;
  }

  newCharacter.sheet.uploads.push(newImage);

  dispatch(updateCharacterFunction(newCharacter._id, newCharacter));
}

export function editImage(
  character,
  defaultImage,
  imageResult,
  updateCharacterFunction,
  dispatch,
  imageItem,
) {
  const newCharacter = { ...character };

  const newImage = { ...imageItem };

  if (!newImage.imageBase64.length) {
    newImage.imageBase64 = defaultImage;
  }

  newCharacter.sheet.uploads.push(newImage);

  dispatch(updateCharacterFunction(newCharacter._id, newCharacter));
}
