import axios from 'axios';
import actionTypes from './ActionTypes';
import { loadUserSuccess } from './userActions';

// homeIp: 'http://192.168.1.134:1240'
// skylabIp: 'http://192.168.0.61:1240'

function loadCharacterSuccess(character) {
  return {
    type: actionTypes.LOAD_CHARACTER,
    character,
  };
}

function loadError(error) {
  return {
    type: actionTypes.LOAD_ERROR,
    error,
  };
}

export function loadCharacter(characterId) {
  debugger;
  return async (dispatch) => {
    const endpoint = 'http://192.168.1.134:1240/character';
    try {
      const { data } = await axios.get(endpoint, { params: { characterId } });
      dispatch(loadCharacterSuccess(data));
    } catch (error) {
      dispatch(loadError(error));
    }
  };
}

export function updateCharacter(characterId, updatedCharacter) {
  return async (dispatch) => {
    const endpoint = 'http://192.168.1.134:1240/character';
    try {
      await axios.patch(endpoint, { characterId, updatedCharacter });
      dispatch(loadCharacter(characterId));
    } catch (error) {
      dispatch(loadError(error));
    }
  };
}

export function deleteCharacter(characterId, user) {
  return async (dispatch) => {
    const characterEndpoint = 'http://192.168.1.134:1240/character';
    const userEndpoint = 'http://192.168.1.134:1240/user';
    try {
      await axios.delete(characterEndpoint, { params: characterId });
      dispatch(loadCharacterSuccess({ character: {} }));
      try {
        const { data } = await axios.patch(userEndpoint, { user });
        dispatch(loadUserSuccess(data));
      } catch (error) {
        dispatch(loadError(error));
      }
    } catch (error) {
      dispatch(loadError(error));
    }
  };
}
