import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  backgroundImage: {
    width: '100%',
    height: '100%',
  },
  notes: {
    width: '100%',
    alignItems: 'center',
    marginTop: '20%',
    marginBottom: '10%',
  },
  character__title: {
    width: '100%',
    alignItems: 'center',
    marginBottom: '5%',
  },
  title__bar: {
    width: '75%',
    height: 4,
    marginTop: 8,
    marginBottom: 8,
    backgroundColor: 'white',
    borderRadius: 10,
  },
  character__name: {
    width: '100%',
    color: 'white',
    fontSize: 32,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  notes__note: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '80%',
    marginTop: '10%',
    padding: 20,
    paddingTop: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.30,
    shadowRadius: 4.65,
    elevation: 8,
  },
  inventory__category__title: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
    marginBottom: 20,
  },
  inventory__category__title__text: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#2089DC',
  },
  addBlueIcon: {
    width: 40,
    height: 40,
  },
  note__row: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    marginTop: 10,
    marginBottom: 10,
  },
  note__row__dot: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '10%',
  },
  dotIcon: {
    width: 18,
    height: 18,
  },
  note__row__entry: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'flex-start',
    width: '90%',
  },
  note__row__entry__text: {
    color: '#224864',
    fontSize: 10,
    fontWeight: 'bold',
  },
});

export default styles;
