import React, { useState } from 'react';
import {
  View, TextInput, TouchableOpacity, Image,
} from 'react-native';
import garbageIcon from '../../../images/garbage-icon.png';

export default function ListRow({
  value, styles, changeListValue, listCategory, list, setItemModalProperties,
}) {
  const [editValue, setEditValue] = useState(value.title);
  return (
    <View style={styles.addItemModal__block__row}>
      <TextInput
        placeholder={listCategory}
        maxLength={30}
        multiline
        style={styles.addItemModal__inputText}
        autoCorrect={false}
        value={editValue}
        onChangeText={(text) => {
          setEditValue(text);
          changeListValue(editValue, text, setItemModalProperties);
        }}
      />
      <TouchableOpacity style={styles.addItemModal__row__garbage}>
        <Image source={garbageIcon} style={styles.garbageIcon} />
      </TouchableOpacity>
    </View>
  );
}
