import axios from 'axios';
import actionTypes from './ActionTypes';

function loadError(error) {
  return {
    type: actionTypes.LOAD_ERROR,
    error,
  };
}

function loadCharactersSuccess(characters) {
  return {
    type: actionTypes.LOAD_CHARACTERS,
    characters,
  };
}

export default function loadCharactersByOwner(ownerId) {
  debugger;
  return async (dispatch) => {
    const endpoint = 'http://192.168.1.134:1240/characters';
    try {
      const { data } = axios.get(endpoint, { ownerId });
      dispatch(loadCharactersSuccess(data));
    } catch (error) {
      dispatch(loadError(error));
    }
  };
}
