/* eslint-disable react/prop-types */
import React, { useState, useEffect } from 'react';
import {
  ScrollView, ImageBackground, View, Text, FlatList,
} from 'react-native';
import { connect } from 'react-redux';
import { loadUser } from '../../redux/actions/userActions';
import styles from './CharacterListStyles';
import absalomBackground from '../../images/absalom-background.png';

function CharacterList({ userItem, dispatch }) {
  const [characterList, setCharacterList] = useState(null);

  useEffect(() => {
    if (!characterList) {
      dispatch(loadUser('5fc3f007332636744ff4a820'));
    }
    setCharacterList(userItem.characters);
  }, [userItem]);

  const characterData = characterList ? (
    characterList.map((character) => {
      const id = Math.random();
      return ({
        id,
        characterName: character.name,
        characterLvl: character.lvl,
        characterGame: character.game,
        characterRace: character.race,
        characterClass: character.characterClass,
      });
    })
  ) : (null);

  const characterBlock = characterList ? (({
    id,
    characterName,
    characterLvl,
    characterGame,
    characterRace,
    characterClass,
  }) => {
    <View>
      <View>
        <Text>{characterName}</Text>
        <Text>{characterLvl}</Text>
      </View>
    </View>;
  }) : (null);

  return (
    <ImageBackground source={absalomBackground} style={styles.backgroundImage}>
      <ScrollView style={styles.container}>
        {characterList ? (
          <View style={styles.characterList}>
            <View style={styles.title__bar} />
            <Text style={styles.characterList__title}>{`${userItem.userName} Characters`}</Text>
            <View style={styles.title__bar} />
            <Text>{characterList[0].name}</Text>
          </View>
        ) : (
          <Text>No user</Text>
        )}
      </ScrollView>
    </ImageBackground>
  );
}

function mapStateToProps({ usersReducer }) {
  return {
    userItem: usersReducer.userItem,
  };
}

export default connect(mapStateToProps)(CharacterList);
