import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
  },
  container: {
    flex: 1,
    marginTop: '10%',
  },
  characterSheet: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    marginTop: '10%',
    marginBottom: '5%',
  },
  characterList__title: {
    width: '100%',
    color: 'white',
    fontSize: 32,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  title__bar: {
    width: '75%',
    height: 4,
    marginTop: 8,
    marginBottom: 8,
    backgroundColor: 'white',
    borderRadius: 10,
  },
  characterSheet__image__container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    marginTop: 10,
    marginBottom: 10,
    borderRadius: 70,
  },
  characterSheet__image: {
    width: 380,
    height: 537.43,
    borderRadius: 10,
  },
  imageModal: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    height: '50%',
    backgroundColor: 'rgba(255, 255, 255, 0)',
    borderRadius: 10,
    shadowColor: 'rgba(255, 255, 255, 0)',
  },
  imageModal__options: {
    display: 'flex',
    flexDirection: 'row-reverse',
    justifyContent: 'space-around',
    alignItems: 'center',
    width: '100%',
    marginBottom: 30,
    position: 'absolute',
    bottom: 0,
    zIndex: 99,
  },
  imageModal__editButton: {
    width: 120,
    marginTop: 20,
    marginBottom: 10,
    backgroundColor: '#3D7BA8',
    borderRadius: 10,
  },
  imageModal__deleteButton: {
    width: 120,
    marginTop: 20,
    marginBottom: 10,
    backgroundColor: '#D13C3C',
    borderRadius: 10,
  },
});

export default styles;
