import React, { useState, useEffect } from 'react';
import {
  Button, Image, View, Platform, Text,
  ImageBackground, ScrollView, TouchableOpacity,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import Constants from 'expo-constants';
import { connect } from 'react-redux';
import addImage from './CharacterSheetFunctions';
import { updateCharacter } from '../../redux/actions/characterActions';
import NavigationBar from '../NavigationBar/NavigationBar';
import styles from './CharacterSheetStyles';
import absalomBackground from '../../images/absalom-background.png';
import defaultImage from '../../images/default-image.png';

function CharacterSheet({
  characterItem, navigation, userId, dispatch,
}) {
  const [character, setCharacter] = useState(characterItem);
  const [images, setImages] = useState([]);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestCameraRollPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        }
      }
    })();
  }, []);

  useEffect(() => {
    setCharacter(characterItem);
    setImages(characterItem.sheet.uploads);
  }, [characterItem]);

  const pickImage = async (imageFunctions) => {
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
      base64: true,
    });

    if (!result.cancelled) {
      const imageBase64 = {
        imageBase64: `data:image/jpg;base64,${result.base64}`,
        uniqueKey: `${performance.now()}${performance.now()}`,
      };
      setImages([...images, imageBase64]);
      addImage(imageFunctions, imageBase64);
    }
  };

  return (
    <ImageBackground
      source={absalomBackground}
      style={styles.backgroundImage}
    >
      <ScrollView style={{ marginTop: 100 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
          <Button
            title="Pick an image from camera roll"
            onPress={() => pickImage({
              character, defaultImage, updateCharacter, dispatch,
            })}
          />
          {images.length ? (
            images.map((image) => (
              <TouchableOpacity
                key={performance.now()}
                onPress={() => console.log(image.uniqueKey)}
              >
                <Image source={{ uri: image.imageBase64 }} style={{ width: 400, height: 400 }} />
              </TouchableOpacity>
            ))
          ) : (
            <Text>Ei</Text>
          )}
        </View>
      </ScrollView>
      <NavigationBar navigation={navigation} userId={userId} dispatch={dispatch} />
    </ImageBackground>
  );
}

function mapStateToProps({ charactersReducer, usersReducer }) {
  return {
    characterItem: charactersReducer.characterItem,
    userId: usersReducer.userId,
  };
}

export default connect(mapStateToProps)(CharacterSheet);
