import React from 'react';
import {
  ScrollView, ImageBackground, TextInput,
  View, Text, TouchableOpacity, Image,
} from 'react-native';
import { Overlay, Button } from 'react-native-elements';
import { connect } from 'react-redux';
import NavigationBar from '../NavigationBar/NavigationBar';
import styles from './CharacterInventoryStyles';
import absalomBackground from '../../images/absalom-background.png';
import addIcon from '../../images/add-icon.png';

function CharacterInventory({
  characterItem, userId, dispatch, navigation,
}) {
  debugger;
  return (
    <ImageBackground source={absalomBackground} style={styles.backgroundImage}>
      {characterItem ? (
        <ScrollView style={styles.container}>
          <View style={styles.inventory}>
            <View style={styles.inventory__title}>
              <View style={styles.title__bar} />
              <Text style={styles.inventory__title__text}>{`${characterItem.name} Inventory`}</Text>
              <View style={styles.title__bar} />
            </View>
            {characterItem.inventory.categories.map((category) => (
              <View style={styles.inventory__category} key={performance.now()}>
                <Text style={styles.inventory__category__title}>{category.title}</Text>
                {category.items.map((item) => {
                  const properties = Object.keys(item);
                  return (
                    <View style={styles.inventory__category__item} key={performance.now()}>
                      <View style={styles.inventory__category__item__title}>
                        <Text
                          style={styles.inventory__category__item__title__text}
                        >
                          {item.itemTitle}
                        </Text>
                      </View>
                      {properties.map((property) => {
                        if (property !== 'itemTitle') {
                          return (
                            <View
                              style={styles.inventory__category__item__row}
                              key={performance.now()}
                            >
                              <Text style={styles.inventory__category__item__text}>{`${property}`}</Text>
                              <Text
                                style={styles.inventory__category__item__text}
                              >
                                {item[property]}

                              </Text>
                            </View>
                          );
                        }
                      })}
                    </View>
                  );
                })}
              </View>
            ))}
          </View>
        </ScrollView>
      ) : (
        <View>
          <Text style={{ marginTop: 40, textAlign: 'center' }}>No Character</Text>
        </View>
      )}
      <Overlay
        name="add character modal"
        isVisible={addCharacterModalView}
        overlayStyle={styles.addCharacterModal}
        backdropStyle={styles.addCharacterModal__background}
        animationType="fade"
        onBackdropPress={() => {
          displayAddCharacterModal(setAddCharacterModalView, addCharacterModalView);
          setNewCharacter(newCharacterModel);
        }}
      >
        <View style={styles.addCharacterModal__block}>
          <Text style={styles.addCharacterModal__title}>
            Add a new character
          </Text>
          <View style={styles.addCharacterModal__row}>
            <Text style={styles.addCharacterModal__text}>Name:</Text>
            <View style={styles.addCharacterModal__input}>
              <TextInput
                style={styles.addCharacterModal__textInput}
                placeholder="Name"
                value={newCharacter.name}
                onChangeText={(text) => setNewCharacter({ ...newCharacter, name: text })}
              />
            </View>
          </View>
          <View style={styles.addCharacterModal__row}>
            <Text style={styles.addCharacterModal__text}>Game:</Text>
            <View style={styles.addCharacterModal__input}>
              <TextInput
                style={styles.addCharacterModal__textInput}
                placeholder="Game"
                value={newCharacter.game}
                onChangeText={(text) => setNewCharacter({ ...newCharacter, game: text })}
              />
            </View>
          </View>
          <View style={styles.addCharacterModal__row}>
            <Text style={styles.addCharacterModal__text}>Race:</Text>
            <View style={styles.addCharacterModal__input}>
              <TextInput
                style={styles.addCharacterModal__textInput}
                placeholder="Race"
                value={newCharacter.race}
                onChangeText={(text) => setNewCharacter({ ...newCharacter, race: text })}
              />
            </View>
          </View>
          <View style={styles.addCharacterModal__row}>
            <Text style={styles.addCharacterModal__text}>Class:</Text>
            <View style={styles.addCharacterModal__input}>
              <TextInput
                style={styles.addCharacterModal__textInput}
                placeholder="Class"
                value={newCharacter.characterClass}
                onChangeText={(text) => setNewCharacter(
                  { ...newCharacter, characterClass: text },
                )}
              />
            </View>
          </View>
          <View style={styles.addCharacterModal__row}>
            <Text style={styles.addCharacterModal__text}>Level:</Text>
            <View style={styles.addCharacterModal__input}>
              <TextInput
                style={styles.addCharacterModal__textInput}
                placeholder="Level"
                value={newCharacter.lvl}
                onChangeText={(text) => setNewCharacter({ ...newCharacter, lvl: text })}
              />
            </View>
          </View>
          <Button
            title="Create Character"
            buttonStyle={styles.buttonConfirmAddCharacterModal}
          />
        </View>
      </Overlay>

      <TouchableOpacity
        style={styles.addIcon__block}
        onPress={() => setAddCharacterModalView(!addCharacterModalView)}
      >
        <Image source={addIcon} style={styles.addIcon} />
      </TouchableOpacity>
      <NavigationBar navigation={navigation} userId={userId} dispatch={dispatch} />
    </ImageBackground>
  );
}

function mapStateToProps({ charactersReducer, usersReducer }) {
  return {
    characterItem: charactersReducer.characterItem,
    userId: usersReducer.userId,
  };
}

export default connect(mapStateToProps)(CharacterInventory);
